(function ($, window, undefined) {
     'use strict';

     var $controls = $('.controls'),
         movie = document.querySelector('#headerMovie'),
         $movie = $('[data-src]'),
         $movieSource = $movie.data('src');

     if (window.matchMedia('(min-width:767px)').matches) {
          $movie.attr('src', $movieSource);
     }

     function toggleMovie(evt) {
          if (movie.paused) {
               $controls.removeClass('paused');
               movie.play();
          } else {
               $controls.addClass('paused');
               movie.pause();
          }
     };

     $controls.on('click', toggleMovie);
})(jQuery, window);
